import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useUser } from './UserContext'; // Import the useUser hook
import './Login.css';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();
    const { setUser } = useUser(); // Use the setUser function from context

    const handleSubmit = async (event) => {
        event.preventDefault();
        const url = 'http://127.0.0.1:5000/authenticate';

        try {
            const response = await fetch(url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ id: username, password: password })
            });

            const data = await response.json();

            if (response.ok && data.success) {
                setUser(data.user); // Set the user data globally
                switch (data.role) {
                    case 'student':
                        navigate('/student', { state: { user: data.user, role: data.role } });
                        break;
                    case 'tutor':
                        navigate('/mentor', { state: { user: data.user } });
                        break;
                    case 'manager':
                        navigate('/manager', { state: { user: data.user } });
                        break;
                    case 'supervisor':
                        navigate('/supervisor', { state: { user: data.user } });
                        break;
                    default:
                        alert('Invalid user type!');
                        break;
                }
            } else {
                alert('Invalid username or password!');
            }
        } catch (error) {
            alert('Error logging in: ' + error.message);
        }
    };

    return (
        <div className="container mt-5">
            <h2>宿舍管理系统登录</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="username">用户名</label>
                    <input
                        type="text"
                        name="username"
                        id="username"
                        placeholder="请输入用户名"
                        value={username}
                        onChange={e => setUsername(e.target.value)}
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">密码</label>
                    <input
                        type="password"
                        name="password"
                        id="password"
                        placeholder="请输入密码"
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                        className="form-control"
                    />
                </div>
                <button type="submit" className="btn btn-primary">登录</button>
            </form>
        </div>
    );
}

export default Login;
