import React, { useState } from 'react';
import './StudentDashboard.css'; // 确保你的CSS文件路径正确

function StudentDashboard() {
    const [name, setName] = useState('');
    const [studentId, setStudentId] = useState('');
    const [result, setResult] = useState(null);

    const handleSearch = (event) => {
        event.preventDefault();
        // 模拟查询逻辑
        const fetchedData = `查询结果: ${name} (学号: ${studentId}) 的宿舍信息...`;
        setResult(fetchedData);
    };

    return (
        <div className="container mt-5">
            <h1 className="mb-4">宿舍查询</h1>
            <form onSubmit={handleSearch} className="mb-3">
                <div className="mb-3">
                    <label htmlFor="name" className="form-label">姓名:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="studentId" className="form-label">学号:</label>
                    <input
                        type="text"
                        className="form-control"
                        id="studentId"
                        value={studentId}
                        onChange={(e) => setStudentId(e.target.value)}
                    />
                </div>
                <button type="submit" className="btn btn-primary">开始查询</button>
            </form>
            {result && <div className="alert alert-info">{result}</div>}
        </div>
    );
}

export default StudentDashboard;
