import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import styles from './Dom_select.module.css';

function Dom_select() {
  const location = useLocation();
  const roomid = location.state ? location.state.roomid : null; // 获取传递的roomid
  const roomsStudentsData = location.state ? location.state.rooms_students : [];
  const userData = location.state ? location.state.user : null;
  console.log(roomid);
  console.log(userData);

  // 将roomid和userData[0]打包成数组
  const dataToSend = [userData[0],roomid];

  // Initial state for room availability based on data received
  const [roomAvailability, setRoomAvailability] = useState({
    A: true,
    B: true,
    C: true,
    D: true
  });

  useEffect(() => {
    // Calculate bed availability based on occupied beds in the data
    const availability = { A: true, B: true, C: true, D: true };
    roomsStudentsData.forEach(student => {
      const occupiedBed = student[9].slice(-1); // Assuming the 10th element has the bed info like "B523A"
      if (availability[occupiedBed] !== undefined) {
        availability[occupiedBed] = false; // Mark as occupied
      }
    });
    setRoomAvailability(availability);
  }, [roomsStudentsData]);

  // Other component state and functions...
  const [selectedRooms, setSelectedRooms] = useState({});

  const handleRoomSelection = (room) => {
    if (!roomAvailability[room]) return;
    setSelectedRooms(prev => ({ ...prev, [room]: !prev[room] }));
  };

  const Room = ({ room }) => (
    <div
      className={`${styles.room} ${!roomAvailability[room] ? styles.unavailable : selectedRooms[room] ? styles.selected : ''}`}
      onClick={() => handleRoomSelection(room)}
    >
      {room}
      {selectedRooms[room] && roomAvailability[room] && <span className={styles.checkmark}>✓</span>}
    </div>
  );

  // 发送数据到服务器
  const sendDataToServer = async () => {
    try {
      const response = await fetch('http://localhost:5000/AddAstudent', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(dataToSend)
      });

      if (response.ok) {
        console.log('Data sent successfully!');
        // 显示成功修改
        alert('成功修改');
      } else {
        console.error('Failed to send data:', response.status);
      }
    } catch (error) {
      console.error('Error sending data:', error);
    }
  };

  return (
    <div className={styles.supervisorDashboard}>
      <div className={styles.welcomeText}>Welcome</div>
      <div className={styles.content}>
        <div className={styles.houseLayout}>
          <div className={styles.house}>
            <div className={styles.row}>
              <Room room="A" />
              <Room room="B" />
            </div>
            <div className={styles.row}>
              <Room room="C" />
              <Room room="D" />
            </div>
          </div>
        </div>
        <div className={styles.welcomeAgainText}>Welcome again</div>
        <div className={styles.buttons}>
          <button onClick={() => console.log('Back')}>Back</button>
          <button onClick={sendDataToServer}>Submit</button> {/* 添加发送数据到服务器的事件 */}
        </div>
      </div>
    </div>
  );
}

export default Dom_select;
