import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function StudentDashboard() {
    const location = useLocation();
    const navigate = useNavigate();
    const userData = location.state ? location.state.user : null;

    const handleSelectClick = () => {
        navigate('/select');  // 导航到选择/修改界面
    };

    return (
        <div className="container mt-5">
            <h1 className="mb-3">学生信息面板</h1>
            {userData ? (
                <div className="card">
                    <div className="card-body">
                        <h5 className="card-title">个人详情</h5>
                        <table className="table">
                            <tbody>
                                <tr>
                                    <th scope="row">姓名</th>
                                    <td>{userData[1]}</td>
                                </tr>
                                <tr>
                                    <th scope="row">学位</th>
                                    <td>{userData[2]}</td>
                                </tr>
                                <tr>
                                    <th scope="row">宿舍</th>
                                    <td>{userData[4]}</td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            ) : (
                <div className="alert alert-warning" role="alert">
                    无用户数据
                </div>
            )}
            <button className="btn btn-primary mt-3" onClick={handleSelectClick}>选择/修改</button>
        </div>
    );
}

export default StudentDashboard;
