import React, { useState } from 'react';
import './dom_select.css';

function Dom_select() {
  // 假设这些状态是从后端获取的
  const [roomAvailability, setRoomAvailability] = useState({
    A: true,
    B: false, // 例如房间 B 不可选
    C: true,
    D: false, // 例如房间 D 不可选
  });

  // 选中状态，用于前端显示
  const [selectedRooms, setSelectedRooms] = useState({});

  // 处理房间选择的函数
  const handleRoomSelection = (room) => {
    // 如果房间不可选，则不进行任何操作
    if (!roomAvailability[room]) return;

    // 否则，切换房间的选中状态
    setSelectedRooms((prevSelectedRooms) => ({
      ...prevSelectedRooms,
      [room]: !prevSelectedRooms[room],
    }));
  };

  const Room = ({ room }) => (
    <div
      className={`room ${!roomAvailability[room] ? 'unavailable' : selectedRooms[room] ? 'selected' : ''}`}
      onClick={() => handleRoomSelection(room)}
    >
      {room}
      {selectedRooms[room] && roomAvailability[room] && <span className="checkmark">✓</span>}
    </div>
  );

  return (
    <div className="supervisor-dashboard">
      <div className="welcome-text">Welcome</div>
      <div className="content">
        <div className="house-layout">
          <div className="house">
            <div className="row">
              <Room room="A" />
              <Room room="B" />
            </div>
            <div className="row">
              <Room room="C" />
              <Room room="D" />
            </div>
          </div>
        </div>
        <div className="welcome-again-text">Welcome again</div>
        <div className="buttons">
          <button onClick={() => console.log('Back')}>Back</button>
          <button onClick={() => console.log('Submit')}>Submit</button>
        </div>
      </div>
    </div>
  );
}

export default Dom_select;
