import React from 'react';
import { useLocation } from 'react-router-dom';
import styles from './SupSelectOutput.module.css'; // 调整路径以匹配你的项目结构

function SupSelectOutput() {
  const location = useLocation();
  const info = location.state?.info;  // 从状态中获取传递的信息
  const role = location.state ? location.state.role : null; // 提取角色信息

  // 准备展示学生信息的内容
  const renderUserInfo = () => {
    if (!info || !info.users || info.users.length === 0) {
      return <p>No information received.</p>;
    }

    return info.users.map((user, index) => (
      <div key={index} className={styles.userInfo}>
        <p>姓名: {user[1]}</p>
        <p>学号: {role === 'Dormitory_Supervisor' ? user[9] : user[0]}</p> {/* 根据角色展示不同的 ID */}
        <p>宿舍名: {role === 'Dormitory_Supervisor' ? user[3] : user[5]}</p> {/* 根据角色展示不同的宿舍名 */}
        <hr /> {/* 分隔每位学生信息 */}
      </div>
    ));
  };

  return (
    <div className={styles.container}>
      <div className={styles.card}>
        <h1 className={styles.title}>Received Information</h1>
        <div className={styles.content}>
          {renderUserInfo()}
        </div>
      </div>
    </div>
  );
}

export default SupSelectOutput;
