import React from 'react';
import { useLocation } from 'react-router-dom';
import styles from './SupSelectOutput.module.css'; // 根据你的项目结构调整路径

function SupSelectOutput() {
  const location = useLocation();
  const info = location.state?.info;  // 从状态中获取传递的信息
  const role = location.state ? location.state.role : null; // 提取角色信息

  console.log('角色信息是:', role[0])
  // 渲染学生信息
  const renderStudentInfo = () => {
    if (!info || !info.users || info.users.length === 0) {
      return <p>No information received.</p>;
    }

    return info.users.map((user, index) => (
      <div key={index} className={styles.userInfo}>
        <p>姓名: {user[1]}</p>
        <p>学号: {user[0]}</p>
        <p>宿舍名: {user[5]}</p>
        <hr /> {/* 分隔每位学生信息 */}
      </div>
    ));
  };

  // 渲染宿舍监管员信息
  const renderSupervisorInfo = () => {
    if (!info || !info.users || info.users.length === 0) {
      return <p>No information received.</p>;
    }

    return info.users.map((user, index) => (
      <div key={index} className={styles.userInfo}>
        <p>姓名: {user[1]}</p>
        <p>舍监ID: {user[0]}</p>
        <p>宿舍名: {user[3]}</p>
        <hr /> {/* 分隔每位学生信息 */}
      </div>
    ));
  };

  // 渲染导师信息
  const renderTutorInfo = () => {
    if (!info || !info.users || info.users.length === 0) {
      return <p>No information received.</p>;
    }

    return info.users.map((user, index) => (
      <div key={index} className={styles.userInfo}>
        <p>姓名: {user[1]}</p>
        <p>导师ID: {user[0]}</p>
        <p>宿舍名: {user[3]}</p>
        <hr /> {/* 分隔每位学生信息 */}
      </div>
    ));
  };

  // 根据角色选择渲染函数
  const renderInfo = () => {
    if (role[0] === 'Dormitory_Supervisor') {
      return renderSupervisorInfo();
    } else if (role[0] === 'Tutor') {
      return renderTutorInfo();
    } else if (role[0] === 'Student') {
      return renderStudentInfo();
    } else {
      return <p>角色信息未知。</p>;
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.card}>
        <h1 className={styles.title}>以下是您想要查看的信息</h1>
        <div className={styles.content}>
          {renderInfo()}
        </div>
      </div>
    </div>
  );
}

export default SupSelectOutput;
