import React, { useState } from 'react';
import './SelectionOutput.css';
import { useLocation } from 'react-router-dom';

function SelectionOutput() {
  const location = useLocation();
  const roomsData = location.state ? location.state.roomsData : [];
  const [selectedBuilding, setSelectedBuilding] = useState(null);

  const groupAndSortRooms = (rooms) => {
    const grouped = {};
    rooms.forEach(room => {
      const building = room[0].charAt(0);
      const floor = parseInt(room[0].substring(1, 2), 10);
      if (!grouped[building]) {
        grouped[building] = {};
      }
      if (!grouped[building][floor]) {
        grouped[building][floor] = [];
      }
      grouped[building][floor].push(room);
    });
    return grouped;
  };

  const groupedRooms = groupAndSortRooms(roomsData);

  const renderRoomCheckboxes = (rooms) => {
    return rooms.map((room, idx) => (
      <div className="checkbox-row" key={idx}>
        <input type="checkbox" id={room[0]} name={room[0]} />
        <label htmlFor={room[0]}>{room[0]}</label>
      </div>
    ));
  };

  const renderFloors = (floors) => {
    return Object.entries(floors).map(([floor, rooms]) => (
      <div key={floor}>
        <h2>Floor {floor}</h2>
        {renderRoomCheckboxes(rooms)}
      </div>
    ));
  };

  return (
    <div className="selection-layout">
      <aside className="sidebar">
        <ul>
          {Object.keys(groupedRooms).map(building => (
            <li key={building} className={selectedBuilding === building ? 'active' : ''} onClick={() => setSelectedBuilding(building)}>
              Building {building}
            </li>
          ))}
        </ul>
      </aside>
      <main className="main-content">
        {selectedBuilding ? (
          <div>
            <h1>{`Building ${selectedBuilding}`}</h1>
            {renderFloors(groupedRooms[selectedBuilding])}
          </div>
        ) : (
          <p>Please select a building from the sidebar.</p>
        )}
      </main>
      <button className="submit-button">Submit</button>
    </div>
  );
}

export default SelectionOutput;
