// Dom_select.js
import React, { useState } from 'react';
import styles from './Dom_select.module.css'; // 使用模块化 CSS

function Dom_select() {
  // 假设这些状态是从后端获取的
  const [roomAvailability, setRoomAvailability] = useState({
    A: true,
    B: false, // 例如房间 B 不可选
    C: true,
    D: false, // 例如房间 D 不可选
  });

  // 选中状态，用于前端显示
  const [selectedRooms, setSelectedRooms] = useState({});

  // 处理房间选择的函数
  const handleRoomSelection = (room) => {
    // 如果房间不可选，则不进行任何操作
    if (!roomAvailability[room]) return;

    // 否则，切换房间的选中状态
    setSelectedRooms((prevSelectedRooms) => ({
      ...prevSelectedRooms,
      [room]: !prevSelectedRooms[room],
    }));
  };

  const Room = ({ room }) => (
    <div
      className={`${styles.room} ${!roomAvailability[room] ? styles.unavailable : selectedRooms[room] ? styles.selected : ''}`}
      onClick={() => handleRoomSelection(room)}
    >
      {room}
      {selectedRooms[room] && roomAvailability[room] && <span className={styles.checkmark}>✓</span>}
    </div>
  );

  // 使用模块化 CSS 的样式名
  return (
    <div className={styles.supervisorDashboard}>
      <div className={styles.welcomeText}>Welcome</div>
      <div className={styles.content}>
        <div className={styles.houseLayout}>
          <div className={styles.house}>
            <div className={styles.row}>
              <Room room="A" />
              <Room room="B" />
            </div>
            <div className={styles.row}>
              <Room room="C" />
              <Room room="D" />
            </div>
          </div>
        </div>
        <div className={styles.welcomeAgainText}>Welcome again</div>
        <div className={styles.buttons}>
          <button className={styles.button} onClick={() => console.log('Back')}>Back</button>
          <button className={styles.button} onClick={() => console.log('Submit')}>Submit</button>
        </div>
      </div>
    </div>
  );
}

export default Dom_select;
