import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './Login.module.css';
import backgroundImage from './bg.png';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        const url = 'http://127.0.0.1:5000/authenticate';

        try {
            const response = await fetch(url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ id: username, password: password })
            });

            const data = await response.json();

            if (response.ok && data.success) {
                switch (data.role) {
                    case 'student':
                        navigate('/student', { state: { user: data.user, role: data.role } });
                        console.log(data.user);
                        break;
                    case 'tutor': // Assuming 'tutor' maps to 'mentor' as per your requirement
                        navigate('/mentor');
                        break;
                    case 'manager':
                        navigate('/manager');
                        break;
                    case 'supervisor':
                        navigate('/supervisor');
                        break;
                    default:
                        alert('Invalid user type!');
                        break;
                }
            } else {
                alert('Invalid username or password!');
            }
        } catch (error) {
            alert('Error logging in: ' + error.message);
        }
    };

    return (
        <div style={{ 
            backgroundImage: `url(${backgroundImage})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center center',
            backgroundRepeat: 'no-repeat',
            minHeight: '100vh', // Full height of the viewport
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center'
        }}>
            <div className={styles.container}>
                <h2 className={styles.title}>宿舍管理系统登录</h2>
                <form onSubmit={handleSubmit} className={styles.form}>
                    <div className={styles.formGroup}>
                        <label htmlFor="username" className={styles.label}>用户名</label>
                        <input
                            type="text"
                            id="username"
                            placeholder="请输入用户名"
                            value={username}
                            onChange={e => setUsername(e.target.value)}
                            className={styles.input}
                        />
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="password" className={styles.label}>密码</label>
                        <input
                            type="password"
                            id="password"
                            placeholder="请输入密码"
                            value={password}
                            onChange={e => setPassword(e.target.value)}
                            className={styles.input}
                        />
                    </div>
                    <button type="submit" className={styles.btnPrimary}>登录</button>
                </form>
            </div>
        </div>
    );
}

export default Login;
