import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';  // Import useLocation

function TutorDashboard() {
    const location = useLocation();
    const { user, role, room, floorData } = location.state || { user: null, role: null, room: null, floorData: null };

    // State to track selected room
    const [selectedRoom, setSelectedRoom] = useState(null);
    // State to store rooms and their students
    const [roomsInfo, setRoomsInfo] = useState({});

    // Parse floor data to organize students by room
    useEffect(() => {
        const rooms = {};
        floorData?.forEach(student => {
            const roomNumber = student[5]; // Assuming room number is the 6th item in the array
            if (!rooms[roomNumber]) {
                rooms[roomNumber] = [];
            }
            rooms[roomNumber].push(student);
        });
        setRoomsInfo(rooms);
    }, [floorData]);

    return (
        <div className="container">
            <h1>Welcome, {user && user[1]}</h1>

            <div>
                <h2>Tutor宿舍信息</h2>
                <p>宿舍名称：{room}</p>
                <p>Tutor名称：{user && user[1]}</p>
            </div>

            <div className="room-info">
                <h2>Tutor负责的楼层学生信息</h2>
                {Object.keys(roomsInfo).map(roomNumber => (
                    <button key={roomNumber} onClick={() => setSelectedRoom(roomNumber)}>
                        Room {roomNumber}
                    </button>
                ))}
                {selectedRoom && (
                    <ul>
                        {roomsInfo[selectedRoom].map((student, index) => (
                            <li key={index}>
                                {student[1]} ({student[2]}) - {student[5]}
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
}

export default TutorDashboard;
