import React, { useState } from 'react';
import './SelectionPage.css'; // 确保正确引入CSS文件

function SelectionPage() {
  const [buildings, setBuildings] = useState([]);
  const [floors, setFloors] = useState([]);
  const [gender, setGender] = useState('');

  const handleBuildingChange = (event) => {
    const selectedBuilding = event.target.value;
    if (buildings.includes(selectedBuilding)) {
      setBuildings(buildings.filter(building => building !== selectedBuilding));
    } else {
      setBuildings([...buildings, selectedBuilding]);
    }
  };

  const handleFloorChange = (event) => {
    const selectedFloor = parseInt(event.target.value, 10);
    if (floors.includes(selectedFloor)) {
      setFloors(floors.filter(floor => floor !== selectedFloor));
    } else {
      setFloors([...floors, selectedFloor]);
    }
  };

  const handleGenderChange = (event) => {
    setGender(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log({ buildings, floors, gender });
    alert(`筛选条件：楼栋-${buildings.join(", ")}, 楼层-${floors.join(", ")}, 性别-${gender}`);
  };

  return (
    <div className="container mt-5">
      <h1>宿舍筛选</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-section">
          <strong>楼栋:</strong>
          {['A', 'B', 'C'].map((building) => (
            <div key={building}>
              <input
                type="checkbox"
                id={`building-${building}`}
                value={building}
                onChange={handleBuildingChange}
                checked={buildings.includes(building)}
              />
              <label htmlFor={`building-${building}`}>{building} 栋</label>
            </div>
          ))}
          <span className="multi-select-tip">(可多选)</span>
        </div>
        <div className="form-section">
          <strong>楼层:</strong>
          {[1, 2, 3, 4, 5, 6, 7, 8].map((floor) => (
            <div key={floor}>
              <input
                type="checkbox"
                id={`floor-${floor}`}
                value={floor}
                onChange={handleFloorChange}
                checked={floors.includes(floor)}
              />
              <label htmlFor={`floor-${floor}`}>{floor}</label>
            </div>
          ))}
          <span className="multi-select-tip">(可多选)</span>
        </div>
        <div className="form-section">
          <strong>性别:</strong>
          <div>
            <input
              type="radio"
              id="gender-male"
              name="gender"
              value="男"
              onChange={handleGenderChange}
              checked={gender === '男'}
            />
            <label htmlFor="gender-male">男</label>
            <input
              type="radio"
              id="gender-female"
              name="gender"
              value="女"
              onChange={handleGenderChange}
              checked={gender === '女'}
            />
            <label htmlFor="gender-female">女</label>
          </div>
        </div>
        <button type="submit" className="btn btn-primary">开始筛选</button>
      </form>
    </div>
  );
}

export default SelectionPage;
